import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-category-badge',
  templateUrl: './category-badge.component.html',
  styleUrls: ['./category-badge.component.css']
})
export class CategoryBadgeComponent {
  @Input() name!: string;
  @Input() percentage!: number;
  @Output() badgeClick = new EventEmitter<string>();

  onClick() {
    this.badgeClick.emit(this.name);
  }
}
<div class="card" (click)="emitSelection()">
  <svg class="arc" width="100" height="100">
    <circle cx="50" cy="50" r="45" stroke="#eee" stroke-width="10" fill="none"/>
    <circle
      cx="50" cy="50" r="45"
      stroke="tomato"
      stroke-width="10"
      fill="none"
      [attr.stroke-dasharray]="percentage * 2.83 + ', 999'"
      transform="rotate(-90 50 50)"
    />
  </svg>
  <div class="label">{{ category }} ({{ percentage }}%)</div>
</div>
