import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-category-badge',
  templateUrl: './category-badge.component.html',
  styleUrls: ['./category-badge.component.css']
})
export class CategoryBadgeComponent {
  @Input() category!: string;
  @Input() percentage!: number;
    @Input() progressColor: string = '#4CAF50';
  @Input() trackColor: string = '#e0e0e0';
  @Input() badgeStrokeWidth: number = 8;
   svgViewBox: string = '0 0 100 50';



    pathD: string = `M ${5 + this.badgeStrokeWidth/2},${50 - this.badgeStrokeWidth/2} A ${50 - 5 - this.badgeStrokeWidth/2},${50 - 5 - this.badgeStrokeWidth/2} 0 0 1 ${100 - 5 - this.badgeStrokeWidth/2},${50 - this.badgeStrokeWidth/2}`;

  // Use fixed values for path and radius for simplicity in this example
  private readonly pathRadius = 43; // Based on 'M 5,48 A 43,43 0 0 1 95,48'
  public readonly trackPathD = `M ${50-this.pathRadius},${50-this.badgeStrokeWidth/2+2} A ${this.pathRadius},${this.pathRadius} 0 0 1 ${50+this.pathRadius},${50-this.badgeStrokeWidth/2+2}`;
  public readonly progressPathD = this.trackPathD;
  public readonly strokeWidth = this.badgeStrokeWidth;
  // Calculated properties for the template
  strokeDasharray: number = 0;
  strokeDashoffset: number = 0;

  @Output() badgeClick = new EventEmitter<string>();

   ngOnChanges(changes: SimpleChanges): void {
    if (changes['percentage'] || changes['badgeStrokeWidth']) {
      this.calculateProgress();
    }
  }

  getRotationStyle(percentage: number) {
  const degrees = (percentage / 100) * 180;
  return {
    transform: `rotate(${degrees}deg)`
  };
}

  onClick() {
    this.badgeClick.emit(this.category);
  }
}
