import { Component, OnInit } from '@angular/core';
import { DataService } from '../services/video.service';
import { Video } from '../models/video.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  categories: { name: string, percentage: number, videos: Video[] }[] = [];
  selectedCategory: string | null = null;

selectedStat: any = null;
totalVideos = 0;
filteredVideos: any[] = [];

onCategorySelected(category: string) {
  this.selectedCategory = category;

  const stat = this.Video.find(c => c.name === category);
  this.selectedStat = stat;
  this.totalVideos = this.allVideos.length;
  this.filteredVideos = this.allVideos.filter(v => v.category === category);
}

  constructor(private youtubeService: DataService) {}

  ngOnInit() {
    const allVideos = this.youtubeService.getGroupedCategoryData();
    const total = Object.values(allVideos).reduce((sum, v) => sum + v.length, 0);

    this.categories = Object.entries(allVideos).map(([name, videos]) => ({
      name,
      percentage: Math.round((videos.length / total) * 100),
      videos
    }));
  }

  onBadgeClick(categoryName: string) {
    this.selectedCategory = categoryName;
  }

  getVideosForCategory(category: string) {
    return this.categories.find(cat => cat.name === category)?.videos || [];
  }
}
<!-- TOP ROW: All categories -->
<div class="category-top-row">
  <app-category-card
    *ngFor="let c of categoryStats"
    [category]="c.name"
    [percentage]="c.percentage"
    (select)="onCategorySelected(c.name)">
  </app-category-card>
</div>

<!-- MAIN CONTENT: Selected card on left, videos on right -->
<div class="selected-view" *ngIf="selectedCategory">
  <div class="selected-card">
    <app-category-card
      [category]="selectedCategory"
      [percentage]="selectedStat?.percentage">
    </app-category-card>
    <div class="details">
      <div><strong>Percent:</strong> {{ selectedStat?.percentage }}%</div>
      <div><strong>Videos:</strong> {{ selectedStat?.count }}/{{ totalVideos }}</div>
    </div>
  </div>

  <div class="video-list-section">
    <app-video-list [videos]="filteredVideos"></app-video-list>
  </div>
</div>
