import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-category-badge',
  templateUrl: './category-badge.component.html',
  styleUrls: ['./category-badge.component.css']
})
export class CategoryBadgeComponent {
  @Input() name!: string;
  @Input() percentage!: number;
  @Output() badgeClick = new EventEmitter<string>();

  onClick() {
    this.badgeClick.emit(this.name);
  }
}
<div class="badge" (click)="onClick()">
  <svg viewBox="0 0 36 18" class="progress">
    <path class="bg" d="M2,18 A16,16 0 0,1 34,18" />
    <path class="fill"
      [attr.d]="'M2,18 A16,16 0 0,1 ' + (2 + (32 * percentage / 100)) + ',18'"
      [attr.stroke-dasharray]="percentage + ', 100'"
    />
  </svg>
  <div class="label">{{ name }}</div>
  <div class="percent">{{ percentage }}%</div>
</div>
